def process_multiple_queries(queries: List[Dict]) -> List[Dict]:
    """여러 쿼리를 병렬로 처리하는 함수"""
    # CPU 코어 수의 75%만 사용
    num_processes = max(1, int(cpu_count() * 0.75))
    print(f"{num_processes}개의 프로세스로 작업을 시작합니다.")
    
    # 각 쿼리에 대한 인자 준비
    args = [(query['query'], query['patents']) for query in queries]
    
    # 멀티프로세싱으로 처리
    with Pool(processes=num_processes) as pool:
        results = pool.map(process_patent_query, args)
    
    return results

